import React from "react";
import { Link } from "react-router-dom";
import data from "../../data/data.json";
import Banner from "../../components/Banner/Banner";

function Home() {
  return (
    <div>
      <Banner />

      <div className="card-container">
        {data.map((property) => (
          <div key={property.id} style={cardStyle}>
            <img src={property.cover} alt="location" style={imageStyle} />
            <div style={textContainerStyle}>
              <p style={titleStyle}>{property.title}</p>
            </div>
          </div>
        ))}
      </div>

      <Link to="/logement">Voir tous les logements</Link>
    </div>
  );
}

const cardStyle = {
  width: "20%",
  marginBottom: "20px",
  boxSizing: "border-box",
  position: "relative",
};

const imageStyle = {
  width: "100%",
  borderRadius: "10px",
  marginBottom: "10px",
};

const textContainerStyle = {
  position: "absolute",
  top: "0",
  left: "0",
  width: "100%",
  padding: "10px",
  backgroundColor: "rgba(255, 255, 255, 0.8)",
  borderRadius: "10px",
  textAlign: "center",
  boxSizing: "border-box",
  transition: "opacity 0.3s ease",
  opacity: "0",
  pointerEvents: "none",
  zIndex: "1",
};

const titleStyle = {
  fontWeight: "bold",
};

export default Home;
