import React from "react";
import backgroundImage from "./Image source 1.png"; // Importez votre image de fond

import "../../styles/_All.scss";
import "./_Banner.scss";

function Banner() {
  const bannerStyle = {
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    height: "223px", // Définissez la hauteur de la bannière
    position: "relative", // Assurez-vous que la position est relative pour le contenu de la bannière
  };

  return (
    <div className="banner" style={bannerStyle}>
      <div className="banner-content">
        <h1>Chez vous, partout et ailleurs</h1>
      </div>
    </div>
  );
}

export default Banner;
