import React, { useState } from "react";
import "./_Carrousel.scss"; // Assurez-vous de créer le fichier CSS pour styliser le carrousel
import left from "./left.png";
import right from "./right.png";

export default function Carrousel({ slides }) {
  const [current, setCurrent] = useState(0); //je définis l'index du premier slide à 0
  const length = slides.length; // longueur du tableau de slides

  const nextSlide = () => {
    setCurrent(current === length - 1 ? 0 : current + 1); // on repart au premier slide quand on arrive au dernier
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1); // on repart au dernier slide quand on est au premier
  };

  return (
    <div className="carousel">
      <img
        src={slides[current]} // Utilisation de l'image actuelle comme source
        alt={`Slide ${current + 1}`}
        className="carousel-image"
      />
      <div className="arrow left-arrow" onClick={prevSlide}>
        {/* Utilisation de l'image gauche comme flèche gauche */}
        <img src={left} alt="Left arrow" />
      </div>
      <div className="arrow right-arrow" onClick={nextSlide}>
        {/* Utilisation de l'image droite comme flèche droite */}
        <img src={right} alt="Right arrow" />
      </div>
    </div>
  );
}
