import React, { useState } from "react";
import "./Carrousel.scss";
import leftArrow from "./left.png";
import rightArrow from "./right.png";

function Carrousel() {
  const [currentSlide, setCurrentSlide] = useState(1);

  const handlePrevSlide = () => {
    setCurrentSlide((prevSlide) => Math.max(1, prevSlide - 1));
  };

  const handleNextSlide = () => {
    setCurrentSlide((prevSlide) => Math.min(4, prevSlide + 1));
  };

  return (
    <section className="carousel">
      <div className="carousel__viewport">
        <div className="carousel__slide" id="carousel__slide1">
          Slide 1 Content
        </div>
        <div className="carousel__slide" id="carousel__slide2">
          Slide 2 Content
        </div>
        <div className="carousel__slide" id="carousel__slide3">
          Slide 3 Content
        </div>
        <div className="carousel__slide" id="carousel__slide4">
          Slide 4 Content
        </div>
      </div>
      <aside className="carousel__navigation">
        <ol className="carousel__navigation-list">
          <li className="carousel__navigation-item">
            <a href="#carousel__slide1" className="carousel__navigation-button">
              Go to slide 1
            </a>
          </li>
          <li className="carousel__navigation-item">
            <a href="#carousel__slide2" className="carousel__navigation-button">
              Go to slide 2
            </a>
          </li>
          <li className="carousel__navigation-item">
            <a href="#carousel__slide3" className="carousel__navigation-button">
              Go to slide 3
            </a>
          </li>
          <li className="carousel__navigation-item">
            <a href="#carousel__slide4" className="carousel__navigation-button">
              Go to slide 4
            </a>
          </li>
        </ol>
      </aside>
      <button className="carousel__prev" onClick={handlePrevSlide}>
        <img src={leftArrow} alt="Previous slide" />
      </button>
      <button className="carousel__next" onClick={handleNextSlide}>
        <img src={rightArrow} alt="Next slide" />
      </button>
    </section>
  );
}

export default Carrousel;
