import React, { useState } from "react";
import "./_Carrousel.scss"; // Assurez-vous de créer le fichier CSS pour styliser le carrousel

function Carousel({ pictures }) {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleClickLeft = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? pictures.length - 1 : prevIndex - 1
    );
  };

  const handleClickRight = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === pictures.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div className="carousel">
      <img
        src={pictures[currentImageIndex]}
        alt={`Image ${currentImageIndex + 1}`}
        className="carousel-image"
      />
      <div className="arrow left-arrow" onClick={handleClickLeft}>
        <img src="left.png" alt="Left arrow" />
      </div>
      <div className="arrow right-arrow" onClick={handleClickRight}>
        <img src="right.png" alt="Right arrow" />
      </div>
    </div>
  );
}

export default Carousel;
