import React, { useState, useRef, useEffect } from "react";
import "./_Collapse.scss";
import vectorIcon from "./Vector.png";

function CollapseItem({ title, text }) {
  const [open, setOpen] = useState(false);
  const contentRef = useRef(null);

  const toggleCollapse = () => {
    setOpen(!open);
  };

  useEffect(() => {
    if (open) {
      // Calculer la hauteur totale du contenu avec le padding
      const contentHeight =
        contentRef.current.scrollHeight +
        contentRef.current.offsetHeight -
        contentRef.current.clientHeight;
      contentRef.current.style.maxHeight = `${contentHeight}px`;
    } else {
      // Retarder le changement de style pour éviter le clignotement
      setTimeout(() => {
        contentRef.current.style.maxHeight = "0";
      }, 10); // Délai correspondant à la transition CSS (0.2s)
    }
  }, [open]);

  return (
    <div className="collapse-item">
      <div className="collapse-header" onClick={toggleCollapse}>
        <h3>{title}</h3>
        <img
          src={vectorIcon}
          alt="Vector"
          className={`chevron ${open ? "open" : ""}`}
          style={{
            transform: `rotate(${open ? "180deg" : "0"})`,
            transition: "transform 0.2s ease",
          }}
        />
      </div>
      <div
        className={`collapse-content ${open ? "open" : ""}`}
        ref={contentRef}
      >
        <ul>{text}</ul>
      </div>
    </div>
  );
}

export default CollapseItem;
