import React from "react";
import backgroundImage from "./Image source 1.png"; // Importez votre image de fond

import "../../styles/_All.scss";
import "./_Banner.scss";

function Banner() {
  const bannerStyle = {
    background: <img src={backgroundImage} alt="img" />,
    backgroundSize: "cover",
    backgroundPosition: "center",
    height: "223px", // Définissez la hauteur de la bannière
    position: "relative", // Assurez-vous que la position est relative pour le contenu de la bannière
  };

  const textStyle = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    color: "#FFFFFF", // Couleur du texte
    textAlign: "center", // Centre le texte horizontalement
  };

  return (
    <div className="banner" style={bannerStyle}>
      <div className="banner-content" style={textStyle}>
        <h1>Chez vous, partout et ailleurs</h1>
      </div>
    </div>
  );
}

export default Banner;
